let getDropdown=(e,t,o)=>{let c=document.querySelectorAll(e),d=document.querySelector(t);c.forEach(e=>{e.addEventListener("click",()=>{c.forEach(e=>{e.classList.remove("active")}),d.textContent=e.textContent,e.classList.add("active")})}),document.addEventListener("click",e=>{const t=document.querySelector(o),c=e.target;c!==t&&(c.closest(".dropdown__filter")||(t.parentNode.classList.remove("active"),t.checked=!1))})},addBorder=e=>{document.querySelector(e).addEventListener("click",t=>{const o=t.target;o&&o.classList.contains(e.replace(/^./,""))&&o.parentNode.classList.toggle("active")})};addBorder(".dropdown__switch"),getDropdown(".dropdown__select-option",".dropdown__filter-selected",".dropdown__switch");